package Calculadora;

public class App {
    public int adicao(int a, int b) {
        return a + b;
    }

 

    public int subtracao(int a, int b) {
        return a - b;
    }

 

    public int multiplicacao(int a, int b) {
        return a * b;
    }

 

    public double dividir(int a, int b) {
        if (b == 0) {
            throw new ArithmeticException("Divisão por zero não é permitida.");
        }
        return (double) a / b;
    }

 

}
